mapper.select:
SELECT o.orders_id FROM Orders o
o.deleted_date IS NULL AND o.customer_id = #{customerId} AND o.order_status IN ('ORDER', 'CANCEL')
AND EXISTS ( SELECT 1 FROM OrderItem oi JOIN Item i ON oi.item_id = i.item_id JOIN Brand b ON i.brand_id = b.brand_id WHERE oi.orders_id = o.orders_id AND MATCH(b.brand_name) AGAINST(#{cond.brandName} IN NATURAL LANGUAGE MODE) > 0 )
AND EXISTS ( SELECT 1 FROM OrderItem oi JOIN Item i ON oi.item_id = i.item_id WHERE oi.orders_id = o.orders_id AND MATCH(i.item_name) AGAINST(#{cond.itemName} IN NATURAL LANGUAGE MODE) > 0 )
AND o.created_date BETWEEN #{cond.fromDate} AND #{cond.toDate}
AND o.merchant_uid < #{cond.merchantUid}
ORDER BY o.merchant_uid DESC LIMIT #{cond.sizePlusOne}
