mapper.select:
SELECT r.recipe_uuid, r.recipe_title AS title, r.recipe_description, r.thumbnail_url AS thumbnail, c.nick_name AS writer, r.recipe_cooking_time, r.recipe_servings, rm.recipe_view, rm.review_cnt, rm.like_cnt FROM Recipe r JOIN Customer c ON r.customer_id = c.customer_id JOIN RecipeMeta rm ON r.recipe_meta_id = rm.recipe_meta_id
r.deleted_date IS NULL
AND r.recipe_servings = #{cond.servings}
AND r.recipe_cooking_time BETWEEN #{cond.cookingTimeFrom} AND #{cond.cookingTimeTo}

SELECT r.recipe_uuid, r.recipe_title AS title, r.recipe_description, r.thumbnail_url AS thumbnail, c.nick_name AS writer, r.recipe_cooking_time, r.recipe_servings, rm.recipe_view, rm.review_cnt, rm.like_cnt FROM Recipe r JOIN RecipeMeta rm ON r.recipe_meta_id = rm.recipe_meta_id JOIN Customer c ON r.customer_id = c.customer_id
r.deleted_date IS NULL
AND r.recipe_uuid < #{cond.nextCursor}
ORDER BY r.created_date DESC LIMIT #{cond.sizePlusOne}
